window.addEventListener("load",function(){document.body.className+=" is-loaded";var b=document.body.getElementsByClassName("c-graph__percentage");for(var i=0;i<b.length;i++){var c=parseInt(b[i].innerHTML.match(/\d+/g)[0]);b[i].setAttribute("data-percent",b[i].innerHTML);b[i].style.width=b[i].innerHTML;if(c>60){b[i].className+=" c-graph__percentage--primary"}else if(c<21){b[i].className+=" c-graph__percentage--exploring";}else{b[i].className+=" c-graph__percentage--rudimentary"}}document.getElementsByClassName("js-skills")[0].addEventListener("click",function(e){if(e.target.classList.contains("js-tag")){e.target.classList.toggle("is-disabled");e.target.dataset.category&&document.getElementsByClassName("c-graph--"+e.target.dataset.category)[0].classList.toggle("is-disabled");if(e.target.dataset.level){var a=document.getElementsByClassName("c-graph__percentage--"+e.target.dataset.level);for(var i=0;i<a.length;i++){a[i].parentNode.classList.toggle("is-disabled")}}}})})
